$(function(){$("#loginform").submit(function(){const d=$(".user");const c=$(".psw");if(b(d)){if(a(c)){return true}else{layui.use("layer",function(){layer.msg("密码不能为空！")});return false}}else{layui.use("layer",function(){layer.msg("请检查用户名的正确性！用户名不能包含中文，是数字和字母的6-10位组合")});return false}});function b(d){const c=d.val().trim();if(/[\u4E00-\u9FA5\uF900-\uFA2D]/.test(c)){return false}if(/[\W]/.test(c)){return false}return !(c.length<6||c.length>10)}function a(c){const d=c.val().trim();return d.length!==0}});